let nodes = [], links = [];
const nodeMap = new Map();

function getNodeKey(owner) {
    return `Owner ID: ${owner.ownerId}`;
}


const processOwners = (owners, bookId) => {
    owners.forEach(owner => {
        const nodeId = getNodeKey(owner);
        if (!nodeMap.has(nodeId)) {
            const node = {id: nodeId, degree: 0, type: owner.type};
            nodes.push(node);
            nodeMap.set(nodeId, node);
        }
    });
};


Object.entries(data).forEach(([bookId, ownersList]) => {
    // 首先处理所有节点
    ownersList.forEach(owners => {
        processOwners(Array.isArray(owners) ? owners : [owners], bookId);
    });

    // 然后处理链接
    for (let i = 0; i < ownersList.length - 1; i++) {
        const currentOwners = Array.isArray(ownersList[i]) ? ownersList[i] : [ownersList[i]];
        const nextOwners = Array.isArray(ownersList[i + 1]) ? ownersList[i + 1] : [ownersList[i + 1]];

        currentOwners.forEach(currentOwner => {
            const currentNodeId = getNodeKey(currentOwner);
            nextOwners.forEach(nextOwner => {
                const nextNodeId = getNodeKey(nextOwner);
                const link = {
                    source: nodeMap.get(currentNodeId),
                    target: nodeMap.get(nextNodeId),
                    value: 1,
                    bookId: bookId
                };
                links.push(link);
            });
        });
    }
});

const color = d3.scaleOrdinal(d3.schemeCategory10);

const drag = simulation => {

    function dragstarted(event) {
        if (!event.active) simulation.alphaTarget(0.3).restart();
        event.subject.fx = event.subject.x;
        event.subject.fy = event.subject.y;
    }

    function dragged(event) {
        event.subject.fx = event.x;
        event.subject.fy = event.y;
    }

    function dragended(event) {
        if (!event.active) simulation.alphaTarget(0);
        event.subject.fx = null;
        event.subject.fy = null;
    }

    return d3.drag()
        .on("start", dragstarted)
        .on("drag", dragged)
        .on("end", dragended);
}




const width = 700; // SVG宽度
const height = 660; // SVG高度

const svg = d3.select("svg")
    .attr("width", width)
    .attr("height", height);

const nodeBookMap = new Map();
const bookLinkMap = new Map();
const combinedLinks = [];
const combinedLinkCount = {};

// 计算每个节点的度数
links.forEach(link => {
    link.source.degree += 1;
    link.target.degree += 1;

    const linkKey = link.source.id + "-" + link.target.id;
    if (combinedLinkCount[linkKey]) {
        combinedLinkCount[linkKey].count++;
        combinedLinkCount[linkKey].bookIds.push(link.bookId);
    } else {
        combinedLinks.push(link);
        combinedLinkCount[linkKey] = { count: 1, bookIds: [link.bookId] };
    }

    if (!nodeBookMap.has(link.source.id)) {
        nodeBookMap.set(link.source.id, new Set());
    }
    nodeBookMap.get(link.source.id).add(link.bookId);

    // 更新目标节点的书籍ID集合
    if (!nodeBookMap.has(link.target.id)) {
        nodeBookMap.set(link.target.id, new Set());
    }
    nodeBookMap.get(link.target.id).add(link.bookId);

    if (!bookLinkMap.has(link.bookId)) {
        bookLinkMap.set(link.bookId, { sourceNodes: new Set(), targetNodes: new Set() });
    }
    bookLinkMap.get(link.bookId).sourceNodes.add(link.source.id);
    bookLinkMap.get(link.bookId).targetNodes.add(link.target.id);
});
const linkForce = d3.forceLink(combinedLinks)
    .id(d => d.id)
    .distance(link => {
        const key = link.source.id + "-" + link.target.id;
        const count = combinedLinkCount[key].count;
        return 100 / count; // 假设基础距离是400，根据链接数量调整
    })
    .strength(0.8);

const chargeForce = d3.forceManyBody()
    .strength(-100);

const simulation = d3.forceSimulation(nodes)
    .force("link", linkForce)
    .force("charge", chargeForce)
    .force("center", d3.forceCenter(width / 2, height / 2));


