function updateLinks() {
    const selectedBookIds = new Set(
        d3.selectAll('input[name="bookId"]:checked').data()
    );
    const showIncoming = d3.select('#showIncomingLinks').property('checked');
    const showOutgoing = d3.select('#showOutgoingLinks').property('checked');

    link.style('opacity', d => {
        const linkKey = d.source.id + "-" + d.target.id;
        const linkInfo = combinedLinkCount[linkKey];
        if (!linkInfo) {
            return 0;
        }

        if (!selectedNodeId) {
            // 检查是否有任何与此链接关联的书籍ID被选中
            return linkInfo.bookIds.some(id => selectedBookIds.has(id)) ? 1 : 0;
        }
        const isSelectedBookId = selectedBookIds.has(d.bookId);
        const isSelectedNode = !selectedNodeId || d.source.id === selectedNodeId || d.target.id === selectedNodeId;
        const isIncoming = d.target.id === selectedNodeId && showIncoming;
        const isOutgoing = d.source.id === selectedNodeId && showOutgoing;
        return isSelectedBookId && isSelectedNode && (isIncoming || isOutgoing) ? 1 : 0;
    })
        .style('pointer-events', d => {
            const isSelectedBookId = selectedBookIds.has(d.bookId);
            const isSelectedNode = !selectedNodeId || d.source.id === selectedNodeId || d.target.id === selectedNodeId;
            return isSelectedBookId && isSelectedNode ? 'all' : 'none';
        });

    selfLoop.style('opacity', d => {
        if (!selectedNodeId) {
            // 如果没有选择节点，则根据选择的书籍ID显示链接
            return selectedBookIds.has(d.bookId) ? 1 : 0;
        }
        const isSelectedBookId = selectedBookIds.has(d.bookId);
        const isSelectedNode = !selectedNodeId || d.source.id === selectedNodeId || d.target.id === selectedNodeId;
        const isIncoming = d.target.id === selectedNodeId && showIncoming;
        const isOutgoing = d.source.id === selectedNodeId && showOutgoing;
        return isSelectedBookId && isSelectedNode && (isIncoming || isOutgoing) ? 1 : 0;
    })
        .style('pointer-events', d => {
            const isSelectedBookId = selectedBookIds.has(d.bookId);
            const isSelectedNode = !selectedNodeId || d.source.id === selectedNodeId || d.target.id === selectedNodeId;
            return isSelectedBookId && isSelectedNode ? 'all' : 'none';
        });

    // 更新事件处理器，只有在opacity为1时才有效
    const updateMouseEvents = selection => {
        selection
            .on("mouseover", (event, d) => {
                const linkKey = d.source.id + "-" + d.target.id;
                const bookIds = combinedLinkCount[linkKey].bookIds.join(", ");
                showTooltip(event.pageX, event.pageY, "Book IDs: " + bookIds);
            })
            .on("mouseout", hideTooltip);

    };

    updateMouseEvents(link);
    updateMouseEvents(selfLoop);

    if (!selectedNodeId) {
        d3.selectAll('#checkboxes label')
            .style('display', 'block');
    }
    else{
        d3.selectAll('#checkboxes label')
            .style('display', 'none')
            .filter(function(bookId) {
                const linkInfo = bookLinkMap.get(bookId);
                if (!linkInfo) {
                    return false;
                }
                if (showIncoming && linkInfo.targetNodes.has(selectedNodeId)) {
                    return true; // 显示与选定节点有传入链接的书籍ID
                }
                if (showOutgoing && linkInfo.sourceNodes.has(selectedNodeId)) {
                    return true; // 显示与选定节点有传出链接的书籍ID
                }
                return false;
            })
            .style('display', 'block');
    }
}

