                ifImprint = timeline.events[i].start_time === "Imprint"
                webSvg.append('text')
                    .attr('class', `timeline-text ${textClass} text-${timelineClass}`)
                    .attr('x', x)
                    .attr('y', y - 10)
                    .attr('text-anchor', 'middle')
                    .text(ifImprint? "":timeline.events[i].start_time);
